INSERT INTO medal_of_honor VALUES (59, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Minnesota, in action during the assault on Fort Fisher, 15 January 1865. Landing on the beach with the assaulting party from his ship, Mills charged up to the palisades and, when more than two_thirds of the men became seized with panic and retreated on the run, risked his life to remain with a wounded officer. With the enemy concentrating his fire on the group, he waited until after dark before assisting the wounded man from the field.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/932/mills-charles.php', '1843, Upster, N.Y.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Mills, Charles', 'U.S. Navy', 'Seaman');
