INSERT INTO medal_of_honor VALUES (59, '', -1, -1, -1, 3, 11, 1842, 'Serving as coxswain on board the U.S.S. Carondelet, Morrison was commended for meritorious conduct in general and especially for his heroic conduct and his inspiring example to the crew in the engagement with the rebel ram Arkansas, Yazoo River, 15 July 1862. When the Carondelet was badly cut up, several of her crew killed, many wounded and others almost suffocated from the effects of escaped steam, Morrison was the leader when boarders were called on deck, and the first to return to the guns and give the ram a broadside as she passed. His presence of mind in time of battle or trial is reported as always conspicuous and encouraging.', '', TRUE, '', 'Lansingburg, N.Y.', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/957/morrison-john-g.php', ' Ireland', 'Yazoo River, 1, LA, USA', -90, 'Morrison, John G.', 'U.S. Navy', 'Coxswain');
