INSERT INTO medal_of_honor VALUES (-1, 'New York', -1, -1, -1, -1, -1, -1, 'Served as boatswain''s mate on board the U.S.S. Metacomet, during action against rebel forts and gunboats and with the ram Tennessee in Mobile Bay, 5 August 1864. Despite damage to his ship and the loss of several men on board as enemy fire raked her decks, Murphy performed his duties with skill and courage throughout a furious 2_hour battle which resulted in the surrender of the rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/972/murphy-patrick.php', '1823, Ireland', 'Mobile Bay, Alabama, USA', -87, 'Murphy, Patrick', 'U.S. Navy', 'Boatswain''s Mate');
