INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Oneida in the engagement at Mobile Bay, 5 August 1864. Acting as powder boy at the 30-pounder Parrott rifle, Naylor had his passing box shot from his hands and knocked overboard where it fell in one of the Calena''s boats which was under the bow. Jumping overboard, Naylor recovered his box, returned to his station and continued to carry out his courageous actions throughout the engagement which resulted in the capture of the rebel ram Tennessee and the damaging of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/980/naylor-david.php', '1843, Thompsonville, N.Y.', 'Mobile Bay, Alabama, USA', -87, 'Naylor, David', 'U.S. Navy', 'Landsman');
