INSERT INTO medal_of_honor VALUES (45, 'Virginia', -1, -1, -1, -1, -1, -1, 'Neil served on board the U.S.S. Agawam, as one of a volunteer crew of a powder boat which was exploded near Fort Fisher, 23 December 1864. The powder boat, towed in by the Wilderness to prevent detection by the enemy, cast off and slowly steamed to within 300 yards of the beach. After fuses and fires had been lit and a second anchor with short scope let go to assure the boat''s tailing inshore, the crew again boarded the Wilderness and proceeded a distance of 12 miles from shore. Less than 2 hours later the explosion took place, and the following day fires were observed still burning at the forts.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/982/neil-john.php', '1837, Newfoundland', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Neil, John', 'U.S. Navy', 'Quarter Gunner');
