INSERT INTO medal_of_honor VALUES (32, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Fort Henry, Crystal River, Fla., 15 June 1863. Reconnoitering on the Crystal River on this date and in charge of a boat from the Fort Henry, Orderly Sgt. Nugent ordered an assault upon a rebel breastwork fortification. In this assault, the orderly sergeant and his comrades drove a guard of 11 rebels into the swamp, capturing their arms and destroying their camp equipage while gallantly withholding fire to prevent harm to a woman among the fugitives. On 30 July 1863, he further proved his courage by capturing a boat off Depot Key, Fla., containing 2 men and a woman with their baggage.', '', TRUE, '', '', '-1--1--1', '?', 28, 'http://www.cmohs.org/recipient-detail/999/nugent-christopher.php', '1840, County of Caven, Ireland', 'Crystal River, FL, USA', -82, 'Nugent, Christopher', 'U.S. Marine Corps', 'Orderly Sergeant');
