INSERT INTO medal_of_honor VALUES (-1, '', 19, 3, 1945, 14, 5, 1904, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty while serving as chaplain on board the U.S.S. Franklin when that vessel was fiercely attacked by enemy Japanese aircraft during offensive operations near Kobe, Japan, on 19 March 1945. A valiant and forceful leader, calmly braving the perilous barriers of flame and twisted metal to aid his men and his ship, Lt. Comdr. O''Callahan groped his way through smoke-filled corridors to the open flight deck and into the midst of violently exploding bombs, shells, rockets, and other armament. With the ship rocked by incessant explosions, with debris and fragments raining down and fires raging in ever-increasing fury, he ministered to the wounded and dying, comforting and encouraging men of all faiths; he organized and led firefighting crews into the blazing inferno on the flight deck; he directed the jettisoning of live ammunition and the flooding of the magazine; he manned a hose to cool hot, armed bombs rolling dangerously on the listing deck, continuing his efforts, despite searing, suffocating smoke which forced men to fall back gasping and imperiled others who replaced them. Serving with courage, fortitude, and deep spiritual strength, Lt. Comdr. O''Callahan inspired the gallant officers and men of the Franklin to fight heroically and with profound faith in the face of almost certain death and to return their stricken ship to port.', '', TRUE, 'Chaplain Corps', 'Massachusetts', '1945-3-19', '?', 34, 'http://www.cmohs.org/recipient-detail/2921/o-callahan-joseph-timothy.php', ' Boston, Mass.', 'Kobe, Hyogo Prefecture, Japan', 135, 'O''Callahan, Joseph Timothy', 'U.S. Navy', 'Commander');
INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the flagship U.S.S. Hartford, during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay on 5 August 1864. Although a patient in the sick bay, O''Connell voluntarily reported at his station at the shell whip and continued to perform his duties with zeal and courage until his right hand was severed by an enemy shellburst.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1005/o-connell-thomas.php', '1842, Ireland', 'Mobile Bay, Alabama, USA', -87, 'O''Connell, Thomas', 'U.S. Navy', 'Coal Heaver');
