INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, -1, -1, -1, 'On 31 March 1865, with a comrade, recaptured a Union officer from a detachment of 9 Confederates, capturing 3 of the detachment and dispersing the remainder, and on 1 April 1865, seized a stand of Confederate colors, killing a Confederate officer in a hand_to_hand contest over the colors and retaining the colors until surrounded by Confederates and compelled to relinquish them.', 'Company A', TRUE, '7th Wisconsin Infantry', 'West Point Township, Columbia County, Wis.', '-1--1--1', '?', 37, 'http://www.cmohs.org/recipient-detail/1006/o-connor-albert.php', 'Canada', 'Gravelly Run, 4, VA, USA', -77, 'O''Connor, Albert', 'U.S. Army', 'Sergeant');
