INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Brooklyn during action against rebel forts and gunboats and with the ram Tennessee in Mobile Bay, 5 August 1864. Despite severe damage to his ship and the loss of several men on board as enemy fire raked the deck, Cpl. Oviatt fought his gun with skill and courage throughout the furious 2_hour battle which resulted in the surrender of the rebel ram Tennessee.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1023/oviatt-miles-m.php', '1841, Cattaraugus County, N.Y.', 'Mobile Bay, Alabama, USA', -87, 'Oviatt, Miles M.', 'U.S. Marine Corps', 'Corporal');
