INSERT INTO medal_of_honor VALUES (11, 'Connecticut', -1, -1, -1, -1, -1, -1, 'Peck served as second_class boy on board the Varuna during an attack on Forts Jackson and St. Philip, 24 April 1862. Acting as powder boy of the after rifle, Peck served gallantly while the Varuna was repeatedly attacked and rammed and finally sunk. This was an extremely close_range action and, although badly damaged, the Varuna delivered shells abaft the Morgan''s armor.', '', TRUE, '', '', '-1--1--1', '?', 44, 'http://www.cmohs.org/recipient-detail/1045/peck-oscar-e.php', '1848, Bridgeport, Conn.', 'Fort Jackson, NY 12965, USA', -74, 'Peck, Oscar E.', 'U.S. Navy', 'Second Class Boy');
