INSERT INTO medal_of_honor VALUES (501, 'Virginia', -1, -1, -1, 10, 10, 1872, 'On board the U.S.S. Iowa off Santiago de Cuba, 20 July 1898. Performing his duty at the risk of serious scalding at the time of the blowing out of the manhole gasket on board the vessel, Penn hauled the fire while standing on a board thrown across a coal bucket 1 foot above the boiling water which was still blowing from the boiler.', '', TRUE, '', '', '-1--1--1', '?', 20, 'http://www.cmohs.org/recipient-detail/2169/penn-robert.php', ' City Point, Va.', 'Santiago de Cuba, Cuba', -75, 'Penn, Robert', 'U.S. Navy', 'Fireman First Class');
