INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the ram, Tennessee in Mobile Bay, 5 August 1864. Serving as a gun captain Phinney showed much presence of mind in managing the gun, and gave much needed encouragement to the crew during the engagement which resulted in the capture of the prize rebel ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1058/phinney-william.php', '1824, Norway', 'Mobile Bay, Alabama, USA', -87, 'Phinney, William', 'U.S. Navy', 'Boatswain''s Mate');
INSERT INTO medal_of_honor VALUES (-1, '', 27, 5, 1969, 1, 11, 1950, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty while serving as a combat engineer with Company B in connection with combat operations against the enemy. Pfc. Phipps was a member of a 2-man combat engineer demolition team assigned to locate and destroy enemy artillery ordnance and concealed firing devices. After he had expended all of his explosives and blasting caps, Pfc. Phipps discovered a 175mm high explosive artillery round in a rice paddy. Suspecting that the enemy had attached the artillery round to a secondary explosive device, he warned other marines in the area to move to covered positions and prepared to destroy the round with a hand grenade. As he was attaching the hand grenade to a stake beside the artillery round, the fuse of the enemy''s secondary explosive device ignited. Realizing that his assistant and the platoon commander were both within a few meters of him and that the imminent explosion could kill all 3 men, Pfc. Phipps grasped the hand grenade to his chest and dived forward to cover the enemy''s explosive and the artillery round with his body, thereby shielding his companions from the detonation while absorbing the full and tremendous impact with his body. Pfc. Phipps'' indomitable courage, inspiring initiative, and selfless devotion to duty saved the lives of 2 marines and upheld the highest traditions of the Marine Corps and the U.S. Naval Service. He gallantly gave his life for his country.', 'Company B, 1st Engineer Battalion', TRUE, '1st Marine Division', 'Culver City, Calif.', '1969-5-27', '?', 10, 'http://www.cmohs.org/recipient-detail/3386/phipps-jimmy-w.php', ' Santa Monica, Calif.', 'Honorary Consulate General of the Republic of the Philippines, ph??ng 19, B?nh Th?nh, H? Ch? Minh, Vietnam', 106, 'Phipps, Jimmy W.', 'U.S. Marine Corps', 'Private First Class');
INSERT INTO medal_of_honor VALUES (-1, '', 31, 12, 1862, -1, -1, -1, 'Voluntarily conveyed, under a heavy fire, information to the commander of a battalion of regular troops by which the battalion was saved from capture or annihilation.', '', TRUE, '18th U.S. Infantry', 'Medina County, Ohio', '1862-12-31', '?', 35, 'http://www.cmohs.org/recipient-detail/1059/phisterer-frederick.php', 'Germany', 'Stone River Drive, Memphis, TN 38128, USA', -89, 'Phisterer, Frederick', 'U.S. Army', 'First Lieutenant');
