INSERT INTO medal_of_honor VALUES (-1, '', 25, 8, 1864, -1, -1, -1, 'While a sergeant and retiring with his company before the rapid advance of the enemy at Gettysburg, he and a companion stopped and carried to a place of safety a wounded and helpless comrade; in this act both he and his companion were severely wounded. A year later, at Reams Station, Va., while commanding a skirmish line, voluntarily assisted in checking a flank movement of the enemy, and while so doing was severely wounded, suffering the loss of an arm.', 'Company A', TRUE, '140th Pennsylvania Infantry', '', '1864-8-25', '?', 39, 'http://www.cmohs.org/recipient-detail/1065/pipes-james.php', 'Green County, Pa.', 'Gettysburg, PA 17325, USA', -77, 'Pipes, James', 'U.S. Army', 'Captain');
