INSERT INTO medal_of_honor VALUES (59, 'New York', -1, -1, -1, -1, -1, -1, 'As seaman on board the U.S.S. Magnolia, St. Marks, Fla., 5 and 6 March 1865. Serving with the Army in charge of Navy howitzers during the attack on St. Marks and throughout this fierce engagement, Pyne, although wounded, made remarkable efforts in assisting transport of the gun, and his coolness and determination in courageously standing by his gun while under the fire of the enemy were a credit to the service to which he belonged.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1097/pyne-george.php', '1841, England', 'St. Marks, FL, USA', -84, 'Pyne, George', 'U.S. Navy', 'Seaman');
