INSERT INTO medal_of_honor VALUES (59, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Minnesota in the assault on Fort Fisher, 15 January 1865. Landing on the beach with the assaulting party from his ship, Cpl. Rannahan advanced to the top of the sandhill and partly through the breach in the palisades despite enemy fire which killed or wounded many officers and men. When more than two_thirds of the men became seized with panic and retreated on the run, he remained with the party until dark when it came safely away, bringing its wounded, its arms and its colors.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1103/rannahan-john.php', '1836, County of Monahan, Ireland', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Rannahan, John', 'U.S. Marine Corps', 'Corporal');
