INSERT INTO medal_of_honor VALUES (-1, 'Maryland', -1, -1, -1, 5, 8, 1913, 'For extraordinary and distinguished gallantry above and beyond the call of duty as Officer-in-Charge of the Engineering Repair Party of the U.S.S. Yorktown in action against enemy Japanese forces in the Battle of the Coral Sea on 8 May 1942. During the severe bombarding of the Yorktown by enemy Japanese forces, an aerial bomb passed through and exploded directly beneath the compartment in which Lt. Ricketts'' battle station was located, killing, wounding or stunning all of his men and mortally wounding him. Despite his ebbing strength, Lt. Ricketts promptly opened the valve of a near-by fireplug, partially led out the fire hose and directed a heavy stream of water into the fire before dropping dead beside the hose. His courageous action, which undoubtedly prevented the rapid spread of fire to serious proportions, and his unflinching devotion to duty were in keeping with the highest traditions of the U.S. Naval Service. He gallantly gave his life for his country.', '', TRUE, '', '', '-1--1--1', '?', -19, 'http://www.cmohs.org/recipient-detail/2964/ricketts-milton-ernest.php', ' Baltimore, Md.', 'Coral Sea', 155, 'Ricketts, Milton Ernest', 'U.S. Navy', 'Lieutenant');
