INSERT INTO medal_of_honor VALUES (17, 'Maryland', -1, -1, -1, -1, -1, -1, 'Served as coxswain on board the U.S.S. Wabash in the engagement at Pocataligo, 22 October 1862. Soliciting permission to accompany the howitzer corps, and performing his duty with such gallantry and presence of mind as to attract the attention of all around him, Ringold, knowing there was a scarcity of ammunition, went through the whole line of fire with his shirt slung over his shoulder filled with fixed ammunition which he had brought from 2 miles to the rear of the lines.', '', TRUE, '', '', '-1--1--1', '?', 34, 'http://www.cmohs.org/recipient-detail/1138/ringold-edward.php', '1827, Baltimore, Md.', 'Pocataligo, Georgia 30633, USA', -83, 'Ringold, Edward', 'U.S. Navy', 'Coxswain');
