INSERT INTO medal_of_honor VALUES (-1, '', 23, 4, 1875, -1, -1, -1, 'With 5 other men he waded in mud and water up the creek to a position directly behind an entrenched Cheyenne position, who were using natural bank pits to good advantage against the main column. This surprise attack from the enemy rear broke their resistance.', 'Company H', TRUE, '6th U.S. Cavalry', '', '1875-4-23', '?', 39, 'http://www.cmohs.org/recipient-detail/1851/robbins-marcus-m.php', 'Elba, Wis.', 'Sappa Creek, Kansas, USA', -100, 'Robbins, Marcus M.', 'U.S. Army', 'Private');
