INSERT INTO medal_of_honor VALUES (82, 'Maine', -1, -1, -1, -1, -1, -1, 'With Acting Ensign James H. Bunting, during the heavy gale which occurred in Pensacola Bay on the night of 19 January 1867, Robinson swam ashore with a line for the purpose of sending off a blowcock, which would facilitate getting up steam and prevent the vessel from stranding, thus voluntarily periling his life to save the vessel and the lives of others.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1969/robinson-john.php', '1840, Cuba', 'Pensacola Bay, Florida, USA', -87, 'Robinson, John', 'U.S. Navy', 'Captain of the Hold');
