INSERT INTO medal_of_honor VALUES (59, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Served as first class fireman on board the U.S.S. Montauk, 21 September 1864. During the night of 21 September when fire was discovered in the magazine lightroom of that vessel, causing a panic and demoralizing the crew, Rountry, notwithstanding the cry of "fire in the magazine," forced his way with hose in hand, through the frightened crowd to the lightroom and put out the flames.', '', TRUE, '', '', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/1164/rountry-john.php', '1840, Massachusetts', '', -1, 'Rountry, John', 'U.S. Navy', 'First Class Fireman');
