INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, 1, 3, 1895, 'During the progress of an insurrection at Quilali, Nicaragua, 6, 7, and 8 January 1928, 1st Lt. Schilt, then a member of a marine expedition which had suffered severe losses in killed and wounded, volunteered under almost impossible conditions to evacuate the wounded by air and transport a relief commanding officer to assume charge of a very serious situation. 1st Lt. Schilt bravely undertook this dangerous and important task and, by taking off a total of 10 times in the rough, rolling street of a partially burning village, under hostile infantry fire on each occasion, succeeded in accomplishing his mission, thereby actually saving 3 lives and bringing supplies and aid to others in desperate need.', '', TRUE, '', 'Illinois', '-1--1--1', '?', 13, 'http://www.cmohs.org/recipient-detail/2474/schilt-christian-frank.php', ' Richland County, Ill.', 'Puente Quilali, Quilal?, Nicaragua', -86, 'Schilt, Christian Frank', 'U.S. Marine Corps', 'First Lieutenant');
