INSERT INTO medal_of_honor VALUES (-1, 'Indiana', -1, -1, -1, 10, 8, 1889, 'For extraordinary heroism and conspicuous intrepidity above and beyond the call of duty during action against enemy Japanese forces off Savo Island on the night of 11-12 October and again on the night of 12-13 November 1942. In the earlier action, intercepting a Japanese Task Force intent upon storming our island positions and landing reinforcements at Guadalcanal, Rear Adm. Scott, with courageous skill and superb coordination of the units under his command, destroyed 8 hostile vessels and put the others to flight. Again challenged, a month later, by the return of a stubborn and persistent foe, he led his force into a desperate battle against tremendous odds, directing close-range operations against the invading enemy until he himself was killed in the furious bombardment by their superior firepower. On each of these occasions his dauntless initiative, inspiring leadership and judicious foresight in a crisis of grave responsibility contributed decisively to the rout of a powerful invasion fleet and to the consequent frustration of a formidable Japanese offensive. He gallantly gave his life in the service of his country.', '', TRUE, '', '', '-1--1--1', '?', -9, 'http://www.cmohs.org/recipient-detail/2988/scott-norman.php', ' Indianapolis, Ind.', 'Guadalcanal, Solomon Islands', 160, 'Scott, Norman', 'U.S. Navy', 'Rear Admiral');
