INSERT INTO medal_of_honor VALUES (-1, 'Ohio', -1, -1, -1, 13, 7, 1915, 'For conspicuous devotion to duty, extraordinary courage and complete disregard of his own life, above and beyond the call of duty, during the attack on the Fleet in Pearl Harbor by Japanese forces on 7 December 1941. The compartment, in the U.S.S. California, in which the air compressor, to which Scott was assigned as his battle station, was flooded as the result of a torpedo hit. The remainder of the personnel evacuated that compartment but Scott refused to leave, saying words to the effect "This is my station and I will stay and give them air as long as the guns are going.''''', '', TRUE, '', '', '-1--1--1', '?', 21, 'http://www.cmohs.org/recipient-detail/2989/scott-robert-r.php', ' Massillon, Ohio', 'Pearl Harbor, HI 96706, USA', -157, 'Scott, Robert R.', 'U.S. Navy', 'Machinist''s Mate First Class');
INSERT INTO medal_of_honor VALUES (55, '', -1, -1, -1, 23, 5, 1877, 'In action with the relief expedition of the Allied forces in China during the battles of 13, 20, 21 and 22 June 1900. June 13: Seach and 6 others were cited for their courage in repulsing an attack by 300 Chinese Imperialist soldiers and Boxer militants with a bayonet charge, thus thwarting a planned massive attack on the entire force. June 20: During a day-long battle, Seach ran across an open clearing, gained cover, and cleaned out nests of Chinese snipers. June 21: During a surprise sabre attack by Chinese cavalrymen, Seach was cited for defending gun emplacements. June 22: Seach and others breached the wall of a Chinese fort, fought their way to the enemy''s guns, and turned the cannon upon the defenders of the fort. Throughout this period and in the presence of the enemy, Seach distinguished himself by meritorious conduct.', '', TRUE, '', 'Massachusetts', '-1--1--1', '?', 41, 'http://www.cmohs.org/recipient-detail/2330/seach-william.php', ' London, England', '20 Nan Shi San Lu, Tiexi Qu, Shenyang Shi, Liaoning Sheng, China, 110023', 123, 'Seach, William', 'U.S. Navy', 'Ordinary Seaman');
