INSERT INTO medal_of_honor VALUES (59, 'Ohio', -1, -1, -1, -1, -1, -1, 'Served as seaman on board the U.S.S. Wabash in the assault on Fort Fisher, 15 January 1865. Advancing gallantly through severe enemy fire while armed only with a revolver and cutlass which made it impossible to return the fire at that range, Shepard succeeded in reaching the angle of the fort and in going on, to be one of the few who entered the fort. When the rest of the body of men to his rear were forced to retreat under a devastating fire, he was forced to withdraw through lack of support and to seek the shelter of one of the mounds near the stockade from which point he succeeded in regaining the safety of his ship.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1230/shepard-louis-c.php', '1843, Ohio', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Shepard, Louis C.', 'U.S. Navy', 'Ordinary Seaman');
