INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Served as quartermaster on board the U.S.S. Oneida in the engagement at Mobile Bay 5 August 1864. Acting as captain of the after 11_inch gun, and wounded in several places, Sheridan remained at his gun until the firing had ceased and then took the place of the signal quartermaster who had been injured by a fall. Recommended for his gallantry and intelligence, Sheridan served courageously throughout this battle which resulted in the capture of the rebel ram Tennessee and the damaging of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1232/sheridan-james.php', '1831, Newark, N.J.', 'Mobile Bay, Alabama, USA', -87, 'Sheridan, James', 'U.S. Navy', 'Quartermaster');
