INSERT INTO medal_of_honor VALUES (59, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Ticonderoga in the attack upon Fort Fisher on 15 January 1865. As captain of No. 2 gun, stationed near the 100-pounder Parrott rifle when it burst into fragments, killing 8 men and wounding 12 more, Shipman promptly recognized the effect produced by the explosion and, despite the carnage surrounding them, and the enemy''s fire, encouraged the men at their guns by exclaiming "Go ahead, boys! This is only the fortunes of war!"', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1238/shipman-william.php', '1831, New York, N.Y.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Shipman, William', 'U.S. Navy', 'Coxswain');
INSERT INTO medal_of_honor VALUES (59, 'New Jersey', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Minnesota, in the assault on Fort Fisher, 15 January 1865. Landing on the beach with the assaulting party from his ship, Pvt. Shivers advanced to the top of the sandhill and partly through the breach in the palisades despite enemy fire which killed or wounded many officers and men. When more than two_thirds of the men became seized with panic and retreated on the run, he remained with the party until dark when it came safely away, bringing its wounded, its arms and its colors.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1239/shivers-john.php', '1830, Canada', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Shivers, John', 'U.S. Marine Corps', 'Private');
