INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, 28, 8, 1924, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty while serving with an assault battalion attached to the 4th Marine Division during the battle of Namur Island, Kwajalein Atoll, Marshall Islands, on 1-2 February 1944. Putting up a brave defense against a particularly violent counterattack by the enemy during invasion operations, Pvt. Sorenson and 5 other marines occupying a shellhole were endangered by a Japanese grenade thrown into their midst. Unhesitatingly, and with complete disregard for his own safety, Pvt. Sorenson hurled himself upon the deadly weapon, heroically taking the full impact of the explosion. As a result of his gallant action, he was severely wounded, but the lives of his comrades were saved. His great personal valor and exceptional spirit of self-sacrifice in the face of almost certain death were in keeping with the highest traditions of the U.S. Naval Service.', '', TRUE, '4th Marine Division', 'Minnesota', '-1--1--1', '?', 9, 'http://www.cmohs.org/recipient-detail/3006/sorenson-richard-keith.php', ' Anoka, Minn.', 'Namur, Kwajalein Atoll, RMI', 167, 'Sorenson, Richard Keith', 'U.S. Marine Corps', 'Private');
