INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Served as seaman on board the U.S.S. Monticello during the reconnaissance of the harbor and water defenses of Wilmington, N.C. 23 to 25 June 1864. Taking part in a reconnaissance of enemy defenses which covered a period of 2 days and nights, Sullivan courageously carried out his duties during this action, which resulted in the capture of a mail carrier and mail, the cutting of a telegraph wire, and the capture of a large group of prisoners. Although in immediate danger from the enemy at all times, Sullivan showed gallantry and coolness throughout this action which resulted in the gaining of much vital information of the rebel defenses.', '', TRUE, '', '', '-1--1--1', '?', 34, 'http://www.cmohs.org/recipient-detail/1321/sullivan-john.php', '1839, New York, N.Y.', 'Wilmington, NC, USA', -77, 'Sullivan, John', 'U.S. Navy', 'Seaman');
