INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay, on 5 August 1864. When an enemy shell exploded in the shellroom, Taylor although wounded went into the room and, with his hand, extinguished the fire from the explosion. He then carried out his duties during the remainder of the prolonged action which resulted in the capture of the prize rebel ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1345/taylor-george.php', '1830, Watertown, N.Y.', 'Mobile Bay, Alabama, USA', -87, 'Taylor, George', 'U.S. Navy', 'Armorer');
