INSERT INTO medal_of_honor VALUES (59, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Minnesota in the assault on Fort Fisher, 15 January 1865. Landing on the beach with the assaulting party from his ship, Private Thompson advanced partly through a breach in the palisades and nearer to the fort than any man from his ship despite enemy fire which killed or wounded many officers and men. When more than two_thirds of the men became seized with panic and retreated on the run, he remained with the party until dark, when it came safely away, bringing its wounded, its arms and its colors.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1364/thompson-henry-a.php', '1841, England', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Thompson, Henry A.', 'U.S. Marine Corps', 'Private');
