INSERT INTO medal_of_honor VALUES (17, '', -1, -1, -1, -1, -1, -1, 'During action of the main squadron of ships against heavily defended Forts Beauregard and Walker on Hilton Head, 7 November 1861. Serving as signal quartermaster on board the U.S.S. Mohican, Thompson steadfastly steered the ship with a steady and bold heart under the batteries; was wounded by a piece of shell but remained at his station until he fell from loss of blood. Legs since amputated.', '', TRUE, '', 'Boston, Mass.', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/1370/thompson-william.php', 'Cape May County, N.J.', 'Hilton Head Island, SC, USA', -80, 'Thompson, William', 'U.S. Navy', 'Signal Quartermaster');
