INSERT INTO medal_of_honor VALUES (-1, '', 2, 5, 1863, -1, -1, -1, 'Volunteered to ascertain the character of approaching troops; rode up so closely as to distinguish the features of the enemy, and as he wheeled to return they opened fire with musketry, the Union troops returning same. Under a terrific fire from both sides Lieutenant Thomson rode back unhurt to the Federal lines, averting a disaster to the Army by his heroic act.', 'Company A', TRUE, '1st New York Cavalry', 'New York, N.Y.', '1863-5-2', '?', 38, 'http://www.cmohs.org/recipient-detail/1372/thomson-clifford.php', '', 'Chancellorsville, VA 22553, USA', -77, 'Thomson, Clifford', 'U.S. Army', 'First Lieutenant');
