INSERT INTO medal_of_honor VALUES (59, 'New Jersey', -1, -1, -1, -1, -1, -1, 'As corporal of the guard on board the U.S.S. Wabash during the assault on Fort Fisher, on 15 January 1865. As 1 of 200 marines assembled to hold a line of entrenchments in the rear of the fort which the enemy threatened to attack in force following a retreat in panic by more than two_thirds of the assaulting ground forces, Cpl. Tomlin took position in line and remained until morning when relief troops arrived from the fort. When one of his comrades was struck down by enemy fire, he unhesitatingly advanced under a withering fire of musketry into an open plain close to the fort and assisted the wounded man to a place of safety.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/1383/tomlin-andrew-j.php', '1844, Goshen, N.J.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Tomlin, Andrew J.', 'U.S. Marine Corps', 'Corporal');
