INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Serving as gunner on board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay, 5 August 1864. Although wounded and ordered below, Ward refused to go, but rendered aid at one of the guns when the crew was disabled. He subsequently remained in the chains, heaving the lead, until nearly caught in the collision with the ram Tennessee. He continued to serve bravely throughout the action which resulted in the capture of the prize ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1435/ward-james.php', '1833, New York, N.Y.', 'Mobile Bay, Alabama, USA', -87, 'Ward, James', 'U.S. Navy', 'Quarter Gunner');
