INSERT INTO medal_of_honor VALUES (45, 'New Jersey', -1, -1, -1, -1, -1, -1, 'Serving as quartermaster on board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the rebel ram Tennessee in Mobile Bay, 5 August 1864. Acting as captain of a gun, Whitfield coolly stood by his gun, holding on to the lock string and waited alongside the rebel ram Tennessee until able to fire the shot that entered her port. Whitfield courageously carried out his duties during the prolonged action which resulted in the capture of the prize ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/1471/whitfield-daniel.php', '1821, Newark, N.J.', 'Fort Morgan, 51 Alabama 180, Gulf Shores, AL 36542, USA', -88, 'Whitfield, Daniel', 'U.S. Navy', 'Quartermaster');
