INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Wilkes served on board U.S. Picket Boat No. 1 in action, 27 October 1864, against the Confederate Ram, Albemarle, which had resisted repeated attacks by our steamers and had kept a large force of vessels employed in watching her. The picket boat, equipped with a spar torpedo, succeeded in passing the enemy pickets within 20 yards without being discovered and them made for the Albemarle under a full head of steam. Immediately taken under fire by the ram, the small boat plunged on, jumped the log boom which encircled the target and exploded its torpedo under the port bow of the ram. The picket boat was destroyed by enemy fire and almost the entire crew taken prisoner or lost.', '', TRUE, '', '', '-1--1--1', '?', 37, 'http://www.cmohs.org/recipient-detail/1481/wilkes-henry.php', '1845, New York, N.Y.', 'United States', -95, 'Wilkes, Henry', 'U.S. Navy', 'Landsman');
INSERT INTO medal_of_honor VALUES (45, '', -1, -1, -1, 6, 6, 1830, 'Served as pilot on board the U.S.S. Signal, Red River, 5 May 1864. Proceeding up the Red River, the U.S.S. Signal engaged a large force of enemy field batteries and sharpshooters, returning their fire until the ship was totally disabled, at which time the white flag was ordered raised. Acting as pilot throughout the battle, Wilkes stood by his wheel until it was disabled in his hands by a bursting enemy shell.', '', TRUE, '', 'Indiana', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/1482/wilkes-perry.php', ' Indiana', 'Red River, NM, USA', -105, 'Wilkes, Perry', 'U.S. Navy', 'Pilot');
