INSERT INTO medal_of_honor VALUES (-1, '', 12, 5, 1864, -1, -1, -1, 'Took the flag from the wounded color bearer and carried it in the charge over the Confederate works, in which charge he also captured the colors of the 56th Virginia (C.S.A.) bringing off both flags in safety.', 'Company E', TRUE, '73d New York Infantry', 'West Meriden, Conn.', '1864-5-12', '?', 38, 'http://www.cmohs.org/recipient-detail/1501/wilson-christopher-w.php', 'Ireland', 'Spotsylvania, VA, USA', -77, 'Wilson, Christopher W.', 'U.S. Army', 'Private');
