INSERT INTO medal_of_honor VALUES (17, 'Ohio', -1, -1, -1, -1, -1, -1, 'Attached to the U.S.S. Minnesota and temporarily serving on the U.S.S. Mount Washington, during action against the enemy in the Nansemond River, 14 April 1863. When the U.S.S. Mount Washington drifted against the bank and all men were driven from the decks by escaping steam following several successive hits which struck her boilers and stopped her engines, Wood boarded the stricken vessel and, despite a strike on the head by a spent ball, continued at his gun for 6 hours as fierce artillery and musketry continued to rake her decks.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/1513/wood-robert-b.php', 'New Garden, Ohio', 'Nansemond River, Suffolk, VA, USA', -76, 'Wood, Robert B.', 'U.S. Navy', 'Coxswain');
