INSERT INTO medal_of_honor VALUES (17, 'California', -1, -1, -1, -1, -1, -1, 'As captain of the gun, serving temporarily on board the U.S.S. Mount Washington, during the Nansemond River action, 14 April 1863. When one of his comrades was struck by a bullet and knocked overboard, Woods fearlessly jumped into the water and swam after him. Before he reached him, the man sank beneath the surface and Woods promptly swam back to the vessel, went to his gun, and fought it to the close of the action. At the close of the battle, he tirelessly cared for the wounded.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/1519/woods-samuel.php', '1835, California', 'Nansemond River, Suffolk, VA, USA', -76, 'Woods, Samuel', 'U.S. Navy', 'Seaman');
