INSERT INTO medal_of_honor VALUES (17, 'New York', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Pittsburg, Mississippi River, 29 April 1863. Engaging the enemy batteries at Grand Gulf, the U.S.S. Pittsburg, although severely damaged and suffering many personnel casualties, continued to fire her batteries until ordered to withdraw. Taking part in a similar action after nightfall, the U.S.S. Pittsburg received further damage, but receiving no personnel casualities in the latter action. Woon showed courage and devotion to duty throughout these bitter engagements.', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/1521/woon-john.php', '1823, England', 'Grand Gulf, MS 39150, USA', -91, 'Woon, John', 'U.S. Navy', 'Boatswain''s Mate');
